package assignment_1;

public class EvenOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 100;
		
		int b = a % 50;
		
		System.out.println(b);
		
		if(b==0){
		
			System.out.println("Even number");
			
		}
		else{
			
			System.out.println("Odd Number");
		}
	}

}
